import React, { useState } from 'react';
import axios from 'axios';

const API_LOGIN_URL = "http://localhost:4000/login";

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();

    // Send the login request to the API
    axios.post(API_LOGIN_URL, { username, password })
      .then((response) => {
        console.log(response.data);
        // Clear the form and any error message
        setUsername('');
        setPassword('');
        setErrorMessage('');
        setSuccessMessage('Successfully logged in');
        // TODO: Handle successful login, e.g., redirect to another page
      })
      .catch((error) => {
        console.error(error);
        // Display the error message, if any
        setErrorMessage('Invalid username or password');
        setSuccessMessage('');
      });
  };

  return (
    <div className="LoginForm">
      <h2>Login</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={handleUsernameChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
      {errorMessage && <p className="error">{errorMessage}</p>}
      {successMessage && <p className="success">{successMessage}</p>}
      <p>Don't have an account? <a href="/signup">Create a new account</a></p>
    </div>
  );
}

export default LoginForm;
