import React, { useState } from 'react'

function BoardgameSelector(props) {
  const [selectedBoardgame, setSelectedBoardgame] = useState('');

  const handleSelectionChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedBoardgame(selectedValue);

    selectedBoardgame = props.boardgames.find((boardgame) => boardgame.id === selectedValue);

    if (selectedBoardGame) {
      console.log('Selected Board Game:', selectedBoardGame.title);
    }
    
  };

  console.log('Selected Board Game:', selectedBoardgame);

  return (
    
    <select value={selectedBoardgame} onChange={handleSelectionChange}>
      {props.boardgames.map((boardgames) => (
        <option key={boardgames.id} value={boardgames.id}>
            {boardgames.title}
        </option>
      ))

      }
    </select>
  )
}

export default BoardgameSelector;
