import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_BOOKINGS_URL = "http://localhost:4000/bookings";
const API_BOARDGAMES_URL = "http://localhost:4000/boardgames";
const API_TABLES_URL = "http://localhost:4000/tables";

function BookingForm() {
  const [boardgames, setBoardgames] = useState([]);
  const [tables, setTables] = useState([]);
  const [bookingData, setBookingData] = useState({
    userId: 1, // to be replaced with the actual user ID from authentication later 
    tableId: null,
    boardgameId: null,
    bookingDate: '',
    bookingStart: '',
    bookingFinish: '',
  });

  // Check if the user is authenticated
  const isAuthenticated = () => {
    const token = localStorage.getItem('authToken');
    return !!token; // Returns true if the token exists, false otherwise
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookingData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    // Send the booking data to the API
    axios.post(API_BOOKINGS_URL, bookingData, {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('authToken')}`,
      },
    })
      .then((response) => {
        console.log(response.data);
        // Clear the form after successful submission
        setBookingData({
          userId: 1,
          tableId: null,
          boardgameId: null,
          bookingDate: '',
          bookingStart: '',
          bookingFinish: '',
        });
      })
      .catch((error) => {
        console.error(error);
        // Handle error if needed
      });
  };

  useEffect(() => {
    // Fetch boardgame data
    axios.get(API_BOARDGAMES_URL)
      .then((response) => setBoardgames(response.data))
      .catch((error) => {
        console.error(error);
        // Handle error if needed
      });

    // Fetch table data
    axios.get(API_TABLES_URL)
      .then((response) => setTables(response.data))
      .catch((error) => {
        console.error(error);
        // Handle error if needed
      });
  }, []);

