import React, { useEffect, useState } from 'react'
import axios from 'axios';

const API_BOOKINGS_URL = "http://localhost:3000/api/v1/bookings";


function Bookings(props) {

  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    // Fetch bookings data
    axios.get(API_BOOKINGS_URL)
      .then((response) => setBookings(response.data))
      .catch((error) => {
        console.error(error);
        // Handle error if needed
      });
  }, []);

  const formatTime = (timeString) => {
    const date = new Date(timeString);
    const hours = date.getHours();
    const minutes = date.getMinutes();
    return `${hours}:${minutes < 10 ? '0' + minutes : minutes}`;
  };

  return (
    <div>
      <h1>Bookings from API</h1>
      {bookings.map((bookings) => {
        return (
            <div key={bookings.id}>
                <p>User ID: {bookings.userId}</p>
                <p>Table: {bookings.tableId}</p>
                <p>Boardgame: {bookings.boardgameId}</p>
                <p>Date: {bookings.bookingDate}</p>
                <p>
                  Start: {formatTime(booking.bookingStart)}
                  <br />
                  End: {formatTime(booking.bookingFinish)}
                </p>
            </div>
        );
      })}
    </div>
  )
}

export default Bookings;
