import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_BOOKINGS_URL = "http://localhost:3000/api/v1/bookings";

function App() {
  const [boardgames, setBoardgames] = useState([]);
  const [tables, setTables] = useState([]);
  const [bookingData, setBookingData] = useState({
    userId: 1, // to be replaced with the actual user ID from authentication later 
    tableId: null,
    boardgameId: null,
    bookingDate: '',
    bookingStart: '',
    bookingFinish: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookingData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    // Send the booking data to the API
    axios.post(API_BOOKINGS_URL, bookingData)
      .then((response) => {
        console.log(response.data);
        // Clear the form after successful submission
        setBookingData({
          userId: 1,
          tableId: null,
          boardgameId: null,
          bookingDate: '',
          bookingStart: '',
          bookingFinish: '',
        });
      })
      .catch((error) => {
        console.error(error);
        // Handle error if needed
      });
  };

  useEffect(() => {
    // Fetch boardgame data
    axios.get(API_BOARDGAMES_URL)
      .then((response) => setBoardgames(response.data))
      .catch((error) => {
        console.error(error);
        // Handle error if needed
      });

    // Fetch table data
    axios.get(API_TABLES_URL)
      .then((response) => setTables(response.data))
      .catch((error) => {
        console.error(error);
        // Handle error if needed
      });
  }, []);

  return (
    <div className="App">
      <h1>Create Booking</h1>
      <form onSubmit={handleFormSubmit}>
        <label>
          Table:
          <select
            name="tableId"
            value={bookingData.tableId || ''}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a table</option>
            {tables.map((table) => (
              <option key={table.id} value={table.id}>
                Table #{table.tableNumber}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Boardgame:
          <select
            name="boardgameId"
            value={bookingData.boardgameId || ''}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a boardgame</option>
            {boardgames.map((boardgame) => (
              <option key={boardgame.id} value={boardgame.id}>
                {boardgame.title}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Booking Date:
          <input
            type="date"
            name="bookingDate"
            value={bookingData.bookingDate}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Booking Start Time:
          <input
            type="time"
            name="bookingStart"
            value={bookingData.bookingStart}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Booking End Time:
          <input
            type="time"
            name="bookingFinish"
            value={bookingData.bookingFinish}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <button type="submit">Create Booking</button>
      </form>
    </div>
  );
}

export default App;
